    <script>
        // Function to get chart colors from data attributes
        function getChartColorsArray(e) {
            if (null !== document.getElementById(e)) {
                var t = document.getElementById(e).getAttribute("data-colors");
                if (t)
                    return (t = JSON.parse(t)).map(function(e) {
                        var t = e.replace(" ", "");
                        if (-1 === t.indexOf(",")) {
                            var r = getComputedStyle(document.documentElement).getPropertyValue(t);
                            return r || t;
                        }
                        var a = e.split(",");
                        return 2 != a.length ? t : "rgba(" +
                            getComputedStyle(document.documentElement).getPropertyValue(a[0]) +
                            "," + a[1] + ")";
                    });
            }
        }
        var pieChart, columnChart;

        document.addEventListener('DOMContentLoaded', function() {
            // Initialize charts
            initializeCharts();

            // Filter functionality
            $('.btn.bg-success').on('click', function() {
                fetchFilteredData();
            });


            $('#districtSelect').on('change', updateBlocks);
        });

        function updateBlocks() {
            const districtId = $('#districtSelect').val();
            if (districtId) {
                $.ajax({
                    url: '/get-blocks/' + districtId,
                    type: 'GET',
                    success: function(data) {
                        const blockSelect = $('#blockSelect');
                        blockSelect.empty();
                        blockSelect.append('<option value="">Select</option>');
                        data.forEach(function(block) {
                            blockSelect.append(`<option value="${block.id}">${block.name}</option>`);
                        });
                    }
                });
            } else {
                $('#blockSelect').empty().append('<option value="">Select</option>');
            }
        }

        // let columnChart
        function initializeCharts(a, b, c) {
            console.log("a,b,c", a, b, c);

            // Pie Chart - Society Distribution by Type
            var pieChartColors = getChartColorsArray("no_of_societies_p_chart");
            if (pieChartColors) {
                var pieChartOptions = {
                    chart: {
                        height: 320,
                        type: "pie",
                        animations: {
                            enabled: true,
                            easing: 'easeinout',
                            speed: 800
                        }
                    },
                    series: [
                        (typeof a !== 'undefined' && a !== null) ? a : {{ $societyTypes[1] ?? 0 }}, // Primary
                        (typeof b !== 'undefined' && b !== null) ? b : {{ $societyTypes[2] ?? 0 }}, // Central
                        (typeof c !== 'undefined' && c !== null) ? c : {{ $societyTypes[3] ?? 0 }} // Apex
                    ],

                    labels: ["Primary", "Central", "Apex"],
                    colors: pieChartColors,
                    legend: {
                        show: true,
                        position: "bottom",
                        horizontalAlign: "center",
                        verticalAlign: "middle",
                        floating: false,
                        fontSize: "14px",
                        offsetX: 0,
                        formatter: function(seriesName, opts) {
                            return seriesName + ": " + opts.w.globals.series[opts.seriesIndex];
                        }
                    },
                    responsive: [{
                        breakpoint: 600,
                        options: {
                            chart: {
                                height: 240
                            },
                            legend: {
                                show: false
                            }
                        },
                    }],
                    tooltip: {
                        y: {
                            formatter: function(value) {
                                return value + " societies";
                            }
                        }
                    }
                };
                if (pieChart) {
                    pieChart.destroy();
                }
                pieChart = new ApexCharts(
                    document.querySelector("#no_of_societies_p_chart"),
                    pieChartOptions
                );
                pieChart.render();
            }
            // Column Chart - Monthly Application Status
            var columnChartColors = getChartColorsArray("column_charts");
            console.log(711, @json($columnChartData));
            if (columnChartColors) {
                var columnChartOptions = {
                    chart: {
