            });
        }

        function resetFilters() {
            // Reset all filter fields
            $('input[name="start"]').val('');
            $('input[name="end"]').val('');
            $('#datepicker4 input').val('');
            $('#datepicker5 input').val('');
            $('#districtSelect').val('').trigger('change');
            $('#blockSelect').val('');
            $('select[name="society_type"]').val('');
            $('select[name="sector_type"]').val('');

            // Fetch fresh data
            fetchFilteredData();
        }
    </script>
@endsection
